package com.tzpt.cloudlibrary.ui.readers;import android.content.Context;import android.text.TextUtils;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import com.tzpt.cloudlibrary.R;import com.tzpt.cloudlibrary.base.adapter.glide.RoundedCornersTransformation;import com.tzpt.cloudlibrary.bean.ActionInfoBean;import com.tzpt.cloudlibrary.utils.glide.GlideApp;import com.tzpt.cloudlibrary.widget.recyclerview.adapter.BaseViewHolder;import com.tzpt.cloudlibrary.widget.recyclerview.adapter.RecyclerArrayAdapter;/** * 活动适配器 * Created by ZhiqiangJia on 2017-08-17. */public class ActionListAdapter extends RecyclerArrayAdapter<ActionInfoBean> {    public ActionListAdapter(Context context) {        super(context);    }    @Override    public BaseViewHolder OnCreateViewHolder(ViewGroup parent, int viewType) {        return new BaseViewHolder<ActionInfoBean>(parent, R.layout.view_our_readers_group_item) {            @Override            public void setData(ActionInfoBean item) {                GlideApp.with(mContext)                        .load(item.mImage)                        .dontAnimate()                        .placeholder(R.drawable.bg_circle_eeeeee)                        .error(R.mipmap.ic_video_error_image)                        .centerCrop()                        .transform(new RoundedCornersTransformation(3, RoundedCornersTransformation.CornerType.ALL))                        .into((ImageView) holder.getView(R.id.readers_img));                holder.setText(R.id.readers_title_tv, TextUtils.isEmpty(item.mTitle) ? "" : item.mTitle)                        .setText(R.id.readers_date_tv, TextUtils.isEmpty(item.mStartDateTime) && TextUtils.isEmpty(item.mEndDateTime)                                ? "" : mContext.getString(R.string.activity_time, item.mStartDateTime, item.mEndDateTime));                setVisible(R.id.readers_sign_up_tag_tv, item.mIsJoin ? View.VISIBLE : View.GONE);            }        };    }}