package com.tzpt.cloudlibrary.ui.readers;import android.content.Context;import android.text.TextUtils;import android.view.View;import android.widget.ImageView;import com.tzpt.cloudlibrary.R;import com.tzpt.cloudlibrary.base.adapter.glide.RoundedCornersTransformation;import com.tzpt.cloudlibrary.base.adapter.recyclerview.EasyRVAdapter;import com.tzpt.cloudlibrary.base.adapter.recyclerview.EasyRVHolder;import com.tzpt.cloudlibrary.bean.ActionInfoBean;import com.tzpt.cloudlibrary.utils.glide.GlideApp;/** * 活动适配器 * Created by ZhiqiangJia on 2017-08-17. */public class ActionListHomeAdapter extends EasyRVAdapter<ActionInfoBean> {    public ActionListHomeAdapter(Context context) {        super(context, R.layout.view_home_our_readers_group_item);        setHasStableIds(true);    }    @Override    protected void onBindData(EasyRVHolder holder, int position, ActionInfoBean item) {        GlideApp.with(mContext)                .load(item.mImage)                .dontAnimate()                .placeholder(R.drawable.bg_circle_eeeeee)                .error(R.mipmap.ic_video_error_image)                .centerCrop()                .transform(new RoundedCornersTransformation(3, RoundedCornersTransformation.CornerType.ALL))                .into((ImageView) holder.getView(R.id.readers_img));        holder.setText(R.id.readers_title_tv, TextUtils.isEmpty(item.mTitle) ? "" : item.mTitle)                .setText(R.id.readers_date_tv, TextUtils.isEmpty(item.mStartDateTime) && TextUtils.isEmpty(item.mEndDateTime)                        ? "" : mContext.getString(R.string.activity_time, item.mStartDateTime, item.mEndDateTime));        holder.setVisible(R.id.readers_sign_up_tag_tv, item.mIsJoin ? View.VISIBLE : View.GONE);    }}